package jgogstad

import cats.effect.IOApp
import cats.syntax.all._
import fs2.Stream

object Day6 extends App {

  val input =
    ""

  def solve(window: Int): Option[Int] =
    input.sliding(window).zipWithIndex.find { case (s, i) => s.distinct.length == s.length }.map(_._2 + window)

  println(solve(4))
  println(solve(14))
}
